const express = require('express');
const multer = require('multer');
const path = require('path');
const fs = require('fs');

const app = express();
const port = process.env.PORT || 3000;

// Serve static files (HTML, CSS, JS) from the 'public' directory
app.use(express.static(path.join(__dirname, 'public')));

// Create "uploads" folder inside "public" if it doesn't exist
const uploadDir = path.join(__dirname, 'public', 'uploads');
if (!fs.existsSync(uploadDir)) {
    fs.mkdirSync(uploadDir, { recursive: true });
}

// Configure Multer for file storage
const storage = multer.diskStorage({
    destination: (req, file, cb) => {
        cb(null, uploadDir);
    },
    filename: (req, file, cb) => {
        cb(null, Date.now() + '-' + file.originalname); // Add timestamp to avoid overwrites
    }
});

const upload = multer({
    storage: storage,
    fileFilter: (req, file, cb) => {
        const allowedTypes = /jpeg|jpg|png|pdf/;
        const extname = allowedTypes.test(path.extname(file.originalname).toLowerCase());
        const mimetype = allowedTypes.test(file.mimetype);

        if (mimetype && extname) {
            cb(null, true);
        } else {
            cb('Error: Only JPEG, JPG, PNG, and PDF files are allowed!');
        }
    }
});

// Serve the homepage from the 'public' directory
app.get('/', (req, res) => {
    res.sendFile(path.join(__dirname, 'public', 'index.html'));
});

// Handle file upload
app.post('/upload', (req, res) => {
    upload.single('uploaded_file')(req, res, (err) => {
        if (err instanceof multer.MulterError) {
            return res.status(400).send(`Multer Error: ${err.message}`);
        } else if (err) {
            return res.status(400).send(`Upload Error: ${err}`);
        }

        if (!req.file) {
            return res.status(400).send('No file selected!');
        }

        // File saved successfully in public/uploads
        res.status(200).send(`File uploaded! Access it at: /uploads/${req.file.filename}`);
    });
});

app.listen(port, () => {
    console.log(`âœ… Server running at http://localhost:${port}`);
    console.log(`ðŸ“‚ Uploads are saved in /public/uploads`);
});
